def buildTitle():
    output = ''
    randomAmount = random.randint(1,6)

    if randomAmount > 3:
        for i in range(randomAmount):
            output += random.sample(connectors, k = 1)[0] + " "
            output += random.sample(possibleNames, k = 1)[0] + " "
        return output
    else:
        for i in range(randomAmount):
            output += random.sample(possibleNames, k = 1)[0] + " "
        return output

df = pd.DataFrame(columns=['ID', 'Movie name', 'Rating', 'Genre', 'User rating', 'User comments'])
for i in range(0, int(input('How many thingys do you need?'))):
    randomIDToGive = ''
    for i in range(5):
        randomIDToGive += random.choice(idPossibilities)
    df = df.append({'ID': randomIDToGive , 'Movie name' : buildTitle() , 'Rating' : random.randint(10,50) / 10, 'Genre' : random.choice(possibleClassifications), 'User rating': 0, 'User comments': "None yet"}, ignore_index=True)


print(df)

df.to_csv("series.csv")